import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import busService from '../services/busService';

const StudentBusSelectionPage = () => {
    const [buses, setBuses] = useState([]);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/');
    };

    useEffect(() => {
        const fetchBuses = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Not authorized');
                    navigate('/student-login');
                    return;
                }
                const res = await busService.getBuses(token);
                setBuses(res.data);
            } catch (err) {
                setError('Could not fetch buses');
            }
        };

        fetchBuses();
    }, [navigate]);

    const handleBusSelection = (busId) => {
        navigate(`/map/${busId}`);
    };

    return (
        <div>
            <button onClick={handleLogout} style={{ position: 'absolute', top: 10, right: 10 }}>Logout</button>
            <h2>Select a Bus to Track</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
                {buses.map((bus) => (
                    <button key={bus.id} onClick={() => handleBusSelection(bus.id)}>
                        {bus.name}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default StudentBusSelectionPage;
