import React, { useState, useEffect, useRef } from 'react';
import busService from '../services/busService';
import io from 'socket.io-client';

const BusSelectionPage = () => {
    const [buses, setBuses] = useState([]);
    const [selectedBus, setSelectedBus] = useState(null);
    const [error, setError] = useState('');
    const socketRef = useRef(null);
    const watchIdRef = useRef(null);

    useEffect(() => {
        const fetchBuses = async () => {
            try {
                const res = await busService.getBuses();
                setBuses(res.data);
            } catch (err) {
                setError('Could not fetch buses');
            }
        };

        fetchBuses();

        // Connect to WebSocket server
        socketRef.current = io('http://localhost:5000'); // Adjust if your backend is elsewhere

        return () => {
            // Disconnect socket and stop watching location on component unmount
            if (socketRef.current) {
                socketRef.current.disconnect();
            }
            if (watchIdRef.current) {
                navigator.geolocation.clearWatch(watchIdRef.current);
            }
        };
    }, []);

    const handleBusSelection = (busId) => {
        setSelectedBus(busId);

        // Stop tracking previous bus if any
        if (watchIdRef.current) {
            navigator.geolocation.clearWatch(watchIdRef.current);
        }

        // Start tracking location
        if (navigator.geolocation) {
            watchIdRef.current = navigator.geolocation.watchPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;
                    socketRef.current.emit('locationUpdate', {
                        busId,
                        lat: latitude,
                        lng: longitude,
                    });
                },
                (err) => {
                    console.error('Geolocation error:', err);
                    setError('Could not get location. Please enable location services.');
                },
                { enableHighAccuracy: true }
            );
        } else {
            setError('Geolocation is not supported by this browser.');
        }
    };

    return (
        <div>
            <h2>Select Your Bus</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {selectedBus ? (
                <div>
                    <h3>Tracking enabled for bus: {selectedBus}</h3>
                    <p>Your location is now being broadcast.</p>
                </div>
            ) : (
                <div>
                    {buses.map((bus) => (
                        <button key={bus.id} onClick={() => handleBusSelection(bus.id)}>
                            {bus.name}
                        </button>
                    ))}
                </div>
            )}
        </div>
    );
};

export default BusSelectionPage;
